mapper.select:
select MONTH(u.create_time) as month ,COUNT(u.id) as count
from u_vip u where u.create_time BETWEEN #{beginTime,jdbcType=DATE}
and #{endTime,jdbcType=DATE} group by MONTH(u.create_time);

select MONTH(u.create_time) as "month" ,COUNT(u.id) as "count" from u_vip u
where u.id in (select s.vip_id from s_move s group by s.vip_id)
group by MONTH(u.create_time);

select mmp as "ages",count(*) as "userCount" from (select case when age is NULL then '0' when age>=1 and age<=12 then '1' when age>=13 and age<=20 then '2' when age>=21 and age<=30 then '3' when age>=31 and age<=40 then '4' when age>=40 then '5' end as mmp,id from u_vip u
where u.id in (select s.vip_id from s_move s group by s.vip_id)
) a group by mmp;

select ps as "ages",count(*) as "userCount" from (select case WHEN position = 'SG' then '得分后卫' WHEN position = 'PG' then '控球后卫' WHEN position = 'SF' then '小前锋' WHEN position = 'PF' then '大前锋' WHEN position = 'C' then '中锋' ELSE NULL end as ps,id from u_vip u where position is not NULL
and u.id in (select s.vip_id from s_move s group by s.vip_id)
) u group by ps;
